INSERT INTO `tbl_partner` VALUES (1,'Ministry of Agriculture and Animal Resources (MINAGRI)','https://www.minagri.gov.rw/','info@minagri.gov.rw','4127',NULL,NULL,'/uploads/media-1703703886007-207664110.jpeg',1),(2,'Rwanda Agriculture and Animal Resources Development Board (RAB)','https://www.rab.gov.rw/','infos@rab.gov.rw','250788385312',NULL,NULL,'/uploads/media-1703704070650-378777789.jpeg',1),(3,'Forum for Agricultural Research in Africa (FARA)','https://www.faraafrica.org/','info@faraafrica.org','00',NULL,NULL,'/uploads/media-1703704406006-450445667.png',1),(4,'Association for Strengthening Agricultural Research in Eastern and Central Africa (ASARECA)','https://www.asareca.org/','secretariat@asareca.org','00',NULL,NULL,'/uploads/media-1703704541342-581761936.jpeg',1),(5,'Eastern Africa Farmers Federation (EAFF)','https://www.eaffu.org/','info@eaffu.org','254204451691',NULL,NULL,'/uploads/media-1703704737808-399216235.jpeg',1),(6,'Mastercard Foundation','https://mastercardfdn.org/','rwanda@mastercardfdn.org','00',NULL,NULL,'/uploads/media-1703705232421-880670795.png',1),(7,'AGRA','https://agra.org/','info@agra.org','00',NULL,NULL,'/uploads/media-1703705856380-384783971.jpeg',1),(8,'UNILAK','https://site.unilak.ac.rw/','info@unilak.ac.rw','00',NULL,NULL,'/uploads/media-1703705970216-359712824.png',1);
